import React from 'react';
import './signUp.css';
import SignIn from './signIn.js';
import { Route, Redirect } from 'react-router'
import { storage } from './firebase';
import Mainprofile from './mainprofile.js';
import { MDBContainer, MDBRow, MDBCol, MDBBtn, MDBInput } from 'mdbreact';


  class SignUp extends React.Component{
     constructor(props){
          super(props);
        this.state = {
          username:'',        
          email:'',
          password:'',
          image:null,
          bio:"",
          url:"",
          toggleSignIn: false,
          id:""
        }
   this.handleChange = this.handleChange.bind(this);

    }

  handleChange(e) {
    if (e.target.files[0]) {
      const image = e.target.files[0];
      this.setState(() => ({ image }));
    }
  }


    handleUpload() {
    const { image } = this.state;
    const uploadTask = storage.ref(`images/${image.name}`).put(image);
     uploadTask.on(
      `state_changed`,
      (snapshot) => {
      },
      (error) => {},
      () => {
        storage.ref(`images`).child(image.name).getDownloadURL().then((url) => {
          console.log(url);
          this.setState({ url });
        });
      }
    );
  }

     yourdata(event){
        this.setState({[event.target.name]: event.target.value });
     }
    server(){
      const token = localStorage.getItem('token');
      var that = this;
      fetch("auth/signUp", {
        method: "POST",
        headers : {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        body: JSON.stringify({"username":this.state.username,
        "email": this.state.email,
        "password" : this.state.password})
      }).then((response) => response.json())
      .then((data)=>{
        console.log(data.token)
        const token = data.token
        console.log(data.user)
        localStorage.setItem('token', token);
        that.setState({
       toggleSignIn: true,
       email:data.user.email
        },() => {
          console.log("hi")
              that.signUp(that);
            })

      })
    }

    signUp(that) {
      fetch("post/", {
        method: "POST",
        headers : {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        body: JSON.stringify({"username":this.state.username,
        "email": this.state.email,
        "password" : this.state.password,
        "bio" : this.state.bio,
        "url": this.state.url
        } 
        )
      }).then((response) => response.json())
      .then((data)=>{
        console.log(data)
        const token = data.token
        localStorage.setItem('token', token);     
          for (var i = 0 ; i < data.length ; i++ ){
          if ( that.state.email === data[i].email){
            if (that.state.password === data[i].password){

              that.setState({
                toggleSignIn: true,
                id:data[i].id
              }
              );
            }
            }
        }
      })
      }




     render(){
      return(<div id="div">
        {!this.state.toggleSignIn ? (
          <div>
       <input type="file" name="image" onChange={this.handleChange} />
       <button onClick={this.handleUpload.bind(this)} className="Button">
            Upload
       </button>
         <img id = "a"
              src={
                this.state.url ||
                'https://i0.wp.com/addisonavenuemarketing.com/wp-content/uploads/2016/07/facebook-avatar.jpg?fit=690%2C435'
              }
             alt="uploaded image"
            />
    <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <form>
            <p className="h5 text-center mb-4">Sign up</p>
            <div className="grey-text">
              <MDBInput
               name = "username" 
               onChange ={this.yourdata.bind(this)}
                label="Your name"
                icon="user"
                group
                type="text"
                validate
                error="wrong"
                success="right"
              />
              <MDBInput
              name = "email"
               onChange ={this.yourdata.bind(this)}
                label="Your email"
                icon="envelope"
                group
                type="email"
                validate
                error="wrong"
                success="right"
              />
              <MDBInput
              name = "bio"
              onChange ={this.yourdata.bind(this)}
                label="bio"
                icon="exclamation-triangle"
                group
                type="text"
                validate
                error="wrong"
                success="right"
              />
              <MDBInput
              name = "password"
              onChange ={this.yourdata.bind(this)}
                label="Your password"
                icon="lock"
                group
                type="password"
                validate
              />
            </div>
            <div className="text-center">
              <MDBBtn color= '#FF7F50' 
              onClick={this.server.bind(this)}
              >Register</MDBBtn>
            </div>
          </form>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
              </div>

        ) : (
          <Mainprofile              
            username = {this.state.username} 
			    	email={this.state.email}
            bio={this.state.bio}
            url={this.state.url}
            id={this.state.id}  
            Redirect to="/mainprofile"
          />
        )}
      </div>

    )}
  }

  export default SignUp;
